    return (
      <>
      <div className="mx-2 "><h5>Yards Gained</h5></div>
          <input type="number" {...register("yards_gained",{ setValueAs: (value) => parseInt(value), })} className="col-10 col-sm-3"/>
      </>
    )

  }
  return null
}

export default GainYards
