    const awayPlayerList = awayTeamData?.OffensePositions?.map((position)=>
      position?.OffenseAthletes?.map((athlete)=>
      <option value={athlete.full_name}>{athlete.jersey} {athlete.full_name}</option>
    ))

    if(value.play_type === "Pass" && value.pass_result === "Complete"){
      if (ballPossession){
        return (
          <>
              <div className="mx-2 mt-2"><h5>Receiver</h5></div>
                  <select {...register("receiver")} className="col-10 col-sm-3">
                    <option key={0} value="-">-</option>
                    {homePlayerList}
                  </select>
          </>
        )
      }else{
        return (
          <>
              <div className="mx-2 mt-2"><h5>Receiver</h5></div>
                  <select {...register("receiver")} className="col-10 col-sm-3">
                    <option key={0} value="-">-</option>
                    {awayPlayerList}
                  </select>
          </>
        )
      }
      }
      return null     
